from typing import Counter, List


class ListNode:
    def __init__(self, val=0, next=None):
        self.val = val
        self.next = next


class Solution:
    def longestPalindrome(self, s: str) -> int:
        answer = 0
        counter_s = Counter(s)
        max_odd = 0
        for c in counter_s:
            if counter_s[c] % 2:
                max_odd=1
            answer += counter_s[c] - counter_s[c] % 2
        return answer + max_odd

solution = Solution()
print(solution.longestPalindrome(""))
